package JavaTutor;

public class LogicalOps {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int subject1 = 30;
		int subject2 = 60;
		// && AND operator
		// || OR operator
		if ((subject1 >= 35) || (subject2 >= 35)) {
			System.out.println("the condition is true"); //
		} else {
			System.out.println("the condition is False");
		}
	}
}